/**
 * @overview クッキーの読み取りをサポートするフィルタです。
 */
"use strict";

var Cookies = require("cookies");

/**
 * クッキーをコンテキストに設定します。
 * 
 * @name Filters#cookies
 * @see Context#cookies
 */
exports.pre = function(context, next) {
	if (context.cookies == null) {
		context.cookies = new Cookies(context.req, context.res);
	}
	next();
};
